const TreeNode = Tree.TreeNode;




const treeAuthor = [{
    title: 'author',
    key: 'title',
    children: [{
        title: '0-0-0',
        key: '0-0-0',

    }, {
        title: '0-0-1',
        key: '0-0-1',

    }, {
        title: '0-0-2',
        key: '0-0-2',
    }],
},
];


export class Filter extends React.Component {
    state = {
        expandedKeys: ['0-0-0', '0-0-1'],
        autoExpandParent: true,
        checkedKeys: ['0-0-0'],
        selectedKeys: [],
    }

    titleList = () => {
        const input  = this.props.input;
        const titleIndex = this.props.titleIndex;
        console.log(titleIndex);
        const result  = [];
        for(let word of input.values()){
            //console.log(word);
            for(let k in titleIndex){
                if (k.startsWith(word)){
                      result.push(k)
                }
            }
        }
        console.log(result);


        const treeTitle = [{
            title: 'title',
            key: 'title',
            children: result.map((item) => {
                return {title:item.value ,key :item.value }
            })
        },
        ];
        console.log(treeTitle);
        return treeTitle;

    }





    onExpand = (expandedKeys) => {
        console.log('onExpand', expandedKeys);
        // if not set autoExpandParent to false, if children expanded, parent can not collapse.
        // or, you can remove all expanded children keys.
        this.setState({
            expandedKeys,
            autoExpandParent: false,
        });
    }

    onCheck = (checkedKeys) => {
        console.log('onCheck', checkedKeys);
        this.setState({ checkedKeys });
    }

    onSelect = (selectedKeys, info) => {
        console.log('onSelect', info);
        this.setState({ selectedKeys });
    }

    renderTreeNodes = (data) => {
        return data.map((item) => {
            if (item.children) {
                return (
                    <TreeNode title={item.title} key={item.title} dataRef={item}>
                        {this.renderTreeNodes(item.children)}
                    </TreeNode>
                );
            }
            return <TreeNode {...item} />;
        });
    }







    render() {
        return (
            <div>
            <Tree
                checkable
                onExpand={this.onExpand}
                expandedKeys={this.state.expandedKeys}
                autoExpandParent={this.state.autoExpandParent}
                onCheck={this.onCheck}
                checkedKeys={this.state.checkedKeys}
                onSelect={this.onSelect}
                selectedKeys={this.state.selectedKeys}
            >
                {this.renderTreeNodes(this.titleList())}
            </Tree>
                <Tree
                    checkable
                    onExpand={this.onExpand}
                    expandedKeys={this.state.expandedKeys}
                    autoExpandParent={this.state.autoExpandParent}
                    onCheck={this.onCheck}
                    checkedKeys={this.state.checkedKeys}
                    onSelect={this.onSelect}
                    selectedKeys={this.state.selectedKeys}
                >
                    {this.renderTreeNodes(treeAuthor)}
                </Tree>
                {/*<Tree*/}
                    {/*checkable*/}
                    {/*onExpand={this.onExpand}*/}
                    {/*expandedKeys={this.state.expandedKeys}*/}
                    {/*autoExpandParent={this.state.autoExpandParent}*/}
                    {/*onCheck={this.onCheck}*/}
                    {/*checkedKeys={this.state.checkedKeys}*/}
                    {/*onSelect={this.onSelect}*/}
                    {/*selectedKeys={this.state.selectedKeys}*/}
                {/*>*/}
                    {/*{this.renderTreeNodes(treeData)}*/}
                {/*</Tree>*/}
                {/*<Tree*/}
                    {/*checkable*/}
                    {/*onExpand={this.onExpand}*/}
                    {/*expandedKeys={this.state.expandedKeys}*/}
                    {/*autoExpandParent={this.state.autoExpandParent}*/}
                    {/*onCheck={this.onCheck}*/}
                    {/*checkedKeys={this.state.checkedKeys}*/}
                    {/*onSelect={this.onSelect}*/}
                    {/*selectedKeys={this.state.selectedKeys}*/}
                {/*>*/}
                    {/*{this.renderTreeNodes(treeData)}*/}
                {/*</Tree>*/}
            </div>
        );
    }
}
